'use strict';

// MODULES //

var bench = require( '@stdlib/bench' );
var randu = require( '@stdlib/math/base/random/randu' );
var isArray = require( '@stdlib/assert/is-array' );
var pkg = require( './../package.json' ).name;
var csub = require( './../lib' );


// MAIN //

bench( pkg, function benchmark( b ) {
	var re1;
	var re2;
	var im1;
	var im2;
	var y;
	var i;

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		re1 = ( randu()*1000.0 ) - 500.0;
		im1 = ( randu()*1000.0 ) - 500.0;
		re2 = ( randu()*1000.0 ) - 500.0;
		im2 = ( randu()*1000.0 ) - 500.0;
		y = csub( re1, im1, re2, im2 );
		if ( y.length === 0 ) {
			b.fail( 'should not be empty' );
		}
	}
	b.toc();
	if ( !isArray( y ) ) {
		b.fail( 'should return an array' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});

bench( pkg+'::memory_reuse', function benchmark( b ) {
	var re1;
	var re2;
	var im1;
	var im2;
	var out;
	var y;
	var i;

	out = new Array( 2 );

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		re1 = ( randu()*1000.0 ) - 500.0;
		im1 = ( randu()*1000.0 ) - 500.0;
		re2 = ( randu()*1000.0 ) - 500.0;
		im2 = ( randu()*1000.0 ) - 500.0;
		y = csub( out, re1, im1, re2, im2 );
		if ( y.length === 0 ) {
			b.fail( 'should not be empty' );
		}
	}
	b.toc();
	if ( !isArray( y ) ) {
		b.fail( 'should return an array' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});

bench( pkg+'::built-in', function benchmark( b ) {
	var re1;
	var re2;
	var im1;
	var im2;
	var y;
	var i;

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		re1 = ( randu()*1000.0 ) - 500.0;
		im1 = ( randu()*1000.0 ) - 500.0;
		re2 = ( randu()*1000.0 ) - 500.0;
		im2 = ( randu()*1000.0 ) - 500.0;
		y = [ re1-re2, im1-im2 ];
		if ( y.length === 0 ) {
			b.fail( 'should not be empty' );
		}
	}
	b.toc();
	if ( !isArray( y ) ) {
		b.fail( 'should return an array' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});

bench( pkg+'::built-in,memory_reuse', function benchmark( b ) {
	var re1;
	var re2;
	var im1;
	var im2;
	var y;
	var i;

	y = new Array( 2 );

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		re1 = ( randu()*1000.0 ) - 500.0;
		im1 = ( randu()*1000.0 ) - 500.0;
		re2 = ( randu()*1000.0 ) - 500.0;
		im2 = ( randu()*1000.0 ) - 500.0;
		y[ 0 ] = re1 - re2;
		y[ 1 ] = im1 - im2;
		if ( y.length === 0 ) {
			b.fail( 'should not be empty' );
		}
	}
	b.toc();
	if ( !isArray( y ) ) {
		b.fail( 'should return an array' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});
